import { useState, useEffect } from "react";
import Axios  from "axios";



function Login() {
	const [EmailReg, setEmailReg] = useState('');
	const [pwdReg, setPwdReg] = useState('');
	const register = () => {
		Axios.post('http://localhost:3001/register', { email: EmailReg, hashpassword: pwdReg, }).then((response) => {
			console.log(response);
		})
	}

	return (

		<div>
			<div className="login">
			<h2>Registration</h2>

<label >Email: </label>

<input type="text" onChange={(e) => { setEmailReg(e.target.value) }} />
<br />
<label >Password: </label>
<input type="password" onChange={(e) => { setPwdReg(e.target.value) }} />
<br />
	<button onClick={register}>Register</button>
			</div>
			<div className="signup">
			<h2>Registration</h2>

			<label >Email: </label>

			<input type="text" onChange={(e) => { setEmailReg(e.target.value) }} />
			<br />
			<label >Password: </label>
			<input type="password" onChange={(e) => { setPwdReg(e.target.value) }} />
			<br />
				<button onClick={register}>Register</button>
			</div>
</div>


	);
}

export default Login;
