import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
	const [Listname, setListname] = useState("");
	const [Listitem, setListitem] = useState("");
	const [todoList, settodoList] = useState([]);
	useEffect(() => {
		Axios.get("http://localhost:3001/api/get").then((response) => {
			settodoList(response.data);
		});
	}, []);

	const submitList = () => {
		Axios.post("http://localhost:3001/api/insert", {
			Listname: Listname,
			Listitem: Listitem,
		});
		settodoList([...todoList, { Listname: Listname, Listitem: Listitem }]);
	};
const deleteReview =(name)=>
{
	Axios.delete(`http://localhost:3001/api/delete/${name}`);
};

	return (
		<div className="App">
			<h1>To do</h1>
			<div className="form">
				<label>List Name</label>
				<input
					type="text"
					name="Listname"
					onChange={(e) => {
						setListname(e.target.value);
					}}
				/>
				<label>List Item</label>
				<input
					type="text"
					name="Listitem"
					onChange={(e) => {
						setListitem(e.target.value);
					}}
				/>
				<button onClick={submitList}>submit</button>
				{todoList.map((val)=>{
					return
				})}
				<div className="card">
					<h1>{val.Listname}</h1>

					<button onClick={(val)=>{deleteReview(val.name)}}>Delete</button>
					<input type="text" id="updateinput"/>
				</div>
			</div>
		</div>
	);
}

export default App;
