import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {

	return (
		<div className="App">
			<h1>To do List</h1>
			<div className="form">
				<label>List Name</label>
				<input type="text" name="Listname" onChange={nameChange} />
				<label>List Item</label>
				<input type="text" name="Listitem" onChange={itemChange} />
				<button onClick={submitList}>submit</button>
				{todoList.map((val) => {
					return (
						<div className="card">
							<h1>{val.Listname}</h1>
							<p>{val.Listitem}</p>
							<button onClick={() => { deleteReview(val.Listname) }}>Delete</button>


						</div>
					);
				})}
			</div>
		</div>
	);
}

export default App;
