import { useState, useEffect } from "react";
import Axios from "axios";



function Login() {
	const [EmailReg, setEmailReg] = useState('');
	const [pwdReg, setPwdReg] = useState('');
	const [EmailCheck, setEmailCheck] = useState('');
	const [pwdCheck, setPwdCheck] = useState('');
	Axios.defaults.withCredentials = true;

	const register = () => {
		Axios.post('http://localhost:3001/register', { email: EmailReg, hashpassword: pwdReg, }).then((response) => {
			console.log(response);
		})
	}
	const login = () => {
		Axios.post('http://localhost:3001/login', { email: EmailCheck, hashpassword: pwdCheck, }).then((response) => {
			if (response.data.message) {
				setLoginStatus(response.data.message)
			} else {
				setLoginStatus(response.data[0].email);

			}

		})
	}

	const [loginStatus, setLoginStatus] = useState('');
	useEffect(() => {
		Axios.get("http://localhost:3001")
	},[])
	return (

		<div>
			<h1>{loginStatus}</h1>
			<div className="login">
				<h2>Login: </h2>
				<label >Email: </label>
				<input type="text" onChange={(e) => { setEmailCheck(e.target.value) }} />
				<br />
				<label >Password: </label>
				<input type="password" onChange={(e) => { setPwdCheck(e.target.value) }} />
				<br />
				<button onClick={login}>Login</button>
			</div>
			<div className="signup">
				<h2>Registration</h2>

				<label >Email: </label>

				<input type="text" onChange={(e) => { setEmailReg(e.target.value) }} />
				<br />
				<label >Password: </label>
				<input type="password" onChange={(e) => { setPwdReg(e.target.value) }} />
				<br />
				<button onClick={register}>Register</button>
			</div>
		</div>


	);
}

export default Login;
